export class EventEmitter {
    constructor() {
        this.events = {};
    }
    on(type, event) {
        if (this.events[type]) {
            this.events[type].push(event);
        }
        else {
            this.events[type] = [event];
        }
    }
    off(type, eventId) {
        if (this.events[type]) {
            this.events[type] = this.events[type].filter(({ id }) => id !== eventId);
        }
    }
    dispatch(type, args) {
        if (this.events[type]) {
            this.events[type].forEach(({ action }) => {
                action(args);
            });
        }
    }
}
