import { curveBasis, curveLinear, pie } from 'd3';
export const pieGenerator = pie()
    .value((d) => d.value)
    .sort(null);
export function getCurveFactory(data) {
    switch (data.connectorCurve) {
        case 'basic': {
            return curveBasis;
        }
        case 'linear': {
            return curveLinear;
        }
    }
    return undefined;
}
